import React from 'react';
import './RecentNews.css';

const newsItems = [
    { date: 'Jan 8, 2024', content: 'Preprint on understanding and computing the true optimal value function of infinite-horizon pendulum swing-up' },
    { date: 'Dec 11, 2023', content: 'Talk at Department of Mathematics, National University of Singapore, thank you Kim-Chuan Toh!' },
    { date: 'Nov 30, 2023', content: 'Preprint with Yukai Tang and Jean-Bernard Lasserre: revisiting the minimum enclosing ellipsoids of set-membership estimation in control and perception, using convex optimization; my presentation at Autonomy Talks can be found here' },
    { date: 'Sep 30, 2023', content: 'Preprint with Xihang Yu: SIM-Sync, certifiably optimal 3D scene reconstruction with learned depth' },
    { date: 'Jul 30, 2023', content: 'Congratulations to Shucheng Kang on getting his paper "Verification and Synthesis of Robust Control Barrier Functions" accepted to IEEE Conference on Decision and Control!' },
    { date: 'Apr 7, 2023', content: 'Object Pose Estimation with Statistical Guarantees accepted to CVPR 2023 as a highlight paper' },
    { date: 'Mar 22, 2023', content: 'New preprint on verification and synthesis of robust control barrier functions: you do not need iterative sum of squares' },
    { date: 'Feb 23, 2023', content: 'Talk at Yau Mathematical Sciences Center, Tsinghua University' },
    { date: 'Jan 1, 2023', content: 'Serving as an Associate Editor for the International Journal of Robotics Research' },
    { date: 'Nov 18, 2022', content: 'Paper on solving large-scale semidefinite relaxations of polynomial optimization accepted to Mathematical Programming! Check out the implementation STRIDE' },
  ];  
  // ... add other news here

const RecentNews = () => {
    return (
        <div className="news-section">
            <h2 className="news-title">News</h2>
            <div className="news-items-container">
                {newsItems.map((item, index) => (
                    <div key={index} className="news-item">
                        <div className="news-date">{item.date}</div>
                        <div className="news-content">{item.content}</div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default RecentNews;
