import React from 'react';
import './ResearchArea.css';
// import researchImage from '../path-to-your-research-image.jpg'; // replace with the path to your image
const researchImage = process.env.PUBLIC_URL + 'svl_robot.jpg';

const ResearchArea = () => {
    return (
        <div className="research-area">
            <img src={researchImage} alt="Research Topic" className="research-image" />
            <div className="research-content">
                <div className="researchtitle"> Multi-Object Multi-Actor (MOMA)</div>
                <p>Multi-Object Multi-Actor (MOMA) is a compositional and hierarchical activity recognition framework for complex activities that involve multiple humans utilizing a variety of objects to accomplish certain tasks...</p>
                <a href="link-to-more-info" className="research-link">Link</a>
            </div>
        </div>
    );
};

export default ResearchArea;
