import React from 'react';
import './PublicationsList.css';

const tagColors = {
    'AI': '#7bdcb5',
    'Deep Learning': '#f78da7',
    'Computer Vision': '#9900ef',
    'Quantum': '#fdcb6e',
    'Computing': '#00b894',
    'Research': '#0984e3',
    'Astrophysics': '#6c5ce7',
    'Universe': '#ffeaa7',
    'Dark Matter': '#d63031',
    // ... define more colors for other tags
  };

const PublicationsList = ({ publications, searchQuery, selectedTags }) => {
    const filteredPublications = publications.filter(pub => {
        const matchesSearch = pub.title.toLowerCase().includes(searchQuery.toLowerCase());
        const matchesTags = selectedTags.length === 0 || selectedTags.every(tag => pub.tags.includes(tag));
        return matchesSearch && matchesTags;
    });

    return (
        <div className="publications-list">
            {filteredPublications.map(pub => (
                <div key={pub.id} className="publication-item">
                    <h3>{pub.title}</h3>
                    <div className="publication-year">{pub.year}</div>
                    <div className="publication-tags">
                        {pub.tags.map(tag => (
                            <span key={tag} className="publication-tag" style={{ backgroundColor: tagColors[tag] }}>
                                {tag}
                            </span>
                        ))}
                    </div>
                </div>
            ))}
        </div>
    );
};


export default PublicationsList;
