import React, { useState } from 'react';
import SearchBar from './PublicationsPage/SearchBar';
import TagFilter from './PublicationsPage/TagFilter';
import PublicationsList from './PublicationsPage/PublicationsList';
import './PublicationsPage.css';

// Dummy data for publications
const allPublications = [
  { id: 1, title: 'On the Nonsmooth Geometry and Neural Approximation of the Optimal Value Function of Infinite-Horizon Pendulum Swing-up', journal_year: 'arXiv preprint 2023', people: ['Haoyu Han', 'Heng Yang'], tags: ['AI', 'Deep Learning', 'Computer Vision']},
  { id: 2, title: 'Uncertainty Quantification of Set-Membership Estimation in Control and Perception: Revisiting the Minimum Enclosing Ellipsoid', journal_year: 'arXiv preprint 2023', people: ['Yukai Tang', 'Jean-Bernard Lasserre', 'Heng Yang'], tags: ['Quantum', 'Computing', 'Research']},
  { id: 3, title: 'SIM-Sync: From Certifiably Optimal Synchronization over the 3D Similarity Group to Scene Reconstruction with Learned Depth', journal_year: 'arXiv preprint 2023', people: ['Xihang Yu', 'Heng Yang'], tags: ['Astrophysics', 'Universe', 'Dark Matter']}
  // ... more publications
];

// List of unique tags extracted from all publications
const uniqueTags = Array.from(new Set(allPublications.flatMap(p => p.tags)));

const PublicationsPage = () => {
    const [searchQuery, setSearchQuery] = useState('');
    const [selectedTags, setSelectedTags] = useState([]);

    return (
        <div className="publications-page">
            <div className="publications-container">
                <SearchBar onSearch={setSearchQuery} />
                <TagFilter onTagSelect={setSelectedTags} selectedTags={selectedTags} tags={uniqueTags} />
                <PublicationsList publications={allPublications} searchQuery={searchQuery} selectedTags={selectedTags} />
            </div>
        </div>
    );
};

export default PublicationsPage;
